    public Character GetCharacter(string name)
    {
        return Characters.FirstOrDefault(c => c.Name.Equals(name, StringComparison.OrdinalIgnoreCase))
               ?? throw new InvalidOperationException($"Character with name '{name}' not found.");
    }
